pragma solidity ^0.4.23;
import { Betting } from "./Betting.sol";

contract MasterKardia {
  address owner;
  modifier onlyAdmin() {
    if (msg.sender == owner) _;
  }

  constructor() public {
    owner = msg.sender;
  }

  function getOwner() public constant returns(address){
    return owner;
  }

  function transferOwner(address newOwner) public onlyAdmin{
    owner = newOwner;
  }

  event onRequestDeploy( address childAddr );

  function requestDeploy() public {
    emit onRequestDeploy(msg.sender);
  }

  event onUpdateResult(address mainAddress, bytes32 candidate, uint256 valueEach);

  function updateResult(bytes32 candidate, uint256 value) public{
    emit onUpdateResult(msg.sender, candidate, value);
  }

  function onBet(address kardiaAddr, bytes32 candidate, uint256 betValue, bytes32 network, uint256 originValue, string voter) public payable{
    Betting betting = Betting(kardiaAddr);
    betting.updateBet(candidate, network, originValue, betValue, voter);
  }

  function addFund() public payable{

  }

}
